          <li
            className="nav-item"
            role="presentation"
            onClick={(e) => {
              openTab()
            }}
          >
            <a
              className={`nav-link ${registerStatus}`}
              id="tab-register"
              data-mdb-toggle="pill"
              href="#pills-register"
              role="tab"
              aria-controls="pills-register"
              aria-selected="false"
            >
              Register
            </a>
          </li>
        </ul>
        {/* <!-- Pills navs --> */}

        {/* <!-- Pills content --> */}
        <div className="tab-content">
          {loginStatus ? <Login /> : <Register />}
        </div>
        {/* <!-- Pills content --> */}
      </div>
    </React.Fragment>
  );
}

export default AuthPage;
